import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './index.css';

interface User {
  id: number;
  name: string;
  email: string;
  lastLogin: string | null;
  status: 'active' | 'blocked';
  registrationDate: string;
  activity: number[];
}

const Home: React.FC = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [selectedIds, setSelectedIds] = useState<number[]>([]);
  const [selectAll, setSelectAll] = useState(false);
//   const [filterDate, setFilterDate] = useState<string>('');
  const [showModal, setShowModal] = useState(false);
  const [deletingIds, setDeletingIds] = useState<number[]>([]);
  const currentUserId = 1; // ID текущего пользователя (заглушка или значение из сессии/контекста)
  const navigate = useNavigate();
  const fetchUsers = async () => {
    try {
      const response = await fetch('http://localhost:8081/users');
      const data: User[] = await response.json();

      const formattedData = data.map((user) => {
        let lastLogin = 'Never';
        if (user.lastLogin) {
          const date = new Date(user.lastLogin);
          if (!Number.isNaN(date.getTime())) {
            lastLogin = date.toLocaleString();
          }
        }
        return {
          ...user,
          lastLogin,
          registrationDate: new Date(user.registrationDate).toLocaleString(),
        };
      });

      setUsers(formattedData);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleDelete = async () => {
    try {
      const response = await fetch('http://localhost:8081/users/delete', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ids: deletingIds }),
      });

      if (!response.ok) {
        throw new Error('Failed to delete users.');
      }

      // Если текущий пользователь удален, выполняем выход
      if (deletingIds.includes(currentUserId)) {
        alert('Your account has been deleted. Redirecting to login...');
        // Очистка состояния авторизации (например, из localStorage)
        localStorage.removeItem('authToken'); // Или другое состояние авторизации
        navigate('/login'); // Перенаправление на страницу входа
        return;
      }

      // Обновляем список пользователей
      const updatedUsers = users.filter(
        (user) => !deletingIds.includes(user.id),
      );
      setUsers(updatedUsers);

      // Сбрасываем выбор и закрываем модальное окно
      setSelectedIds([]);
      setSelectAll(false);
      setShowModal(false);

      alert('Selected users have been deleted.');
    } catch (error) {
      console.error('Error deleting users:', error);
      alert('Failed to delete users. Please try again.');
    }
  };
  const openDeleteModal = () => {
    if (selectedIds.length === 0) {
      console.warn('No users selected for deletion.');
      return;
    }
    setDeletingIds(selectedIds);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setDeletingIds([]);
  };

  const filteredUsers = filterDate
    ? users.filter((user) =>
        new Date(user.lastLogin || '')
          .toLocaleDateString()
          .includes(filterDate),
      )
    : users;

  return (
    <div className="container mt-4">
      <h1>User Table</h1>

      {/* Toolbar */}
      <div className="toolbar mb-4">
        <div className="d-flex justify-content-start mb-3">
          {/* <div className="me-3">
            <input
              id="filter-date"
              type="date"
              value={filterDate}
              onChange={(e) => setFilterDate(e.target.value)}
              className="form-control"
            />
          </div> */}

          <button
            type="button"
            className="btn btn-primary me-2"
            onClick={() => console.log('Block users:', selectedIds)}
          >
            Block
          </button>
          <button
            type="button"
            className="btn btn-outline-secondary me-2"
            onClick={() => console.log('Unblock users:', selectedIds)}
            aria-label="Unblock users"
          >
            <i className="bi bi-unlock" />
          </button>
          <button
            type="button"
            className="btn btn-outline-danger"
            onClick={openDeleteModal}
            aria-label="Delete users"
          >
            <i className="bi bi-trash" />
          </button>
        </div>
      </div>

      {/* Table */}
      <table className="table table-striped table-bordered">
        <thead>
          <tr>
            <th>
              <input
                type="checkbox"
                checked={selectAll}
                onChange={() => {
                  setSelectAll(!selectAll);
                  setSelectedIds(!selectAll ? users.map((u) => u.id) : []);
                }}
                aria-label="Select all users" // Добавляем aria-label
              />
            </th>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Last Login</th>
            <th>Status</th>
            <th>Registration Date</th>
          </tr>
        </thead>
        <tbody>
          {filteredUsers.map((user) => (
            <tr key={user.id}>
              <td>
                <input
                  type="checkbox"
                  checked={selectedIds.includes(user.id)}
                  onChange={() => {
                    setSelectedIds((prev) =>
                      prev.includes(user.id)
                        ? prev.filter((id) => id !== user.id)
                        : [...prev, user.id],
                    );
                  }}
                  aria-label={`Select user with ID ${user.id}`} // Добавляем aria-label
                />
              </td>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.lastLogin}</td>
              <td>{user.status === 'active' ? 'Active' : 'Blocked'}</td>
              <td>{user.registrationDate}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal */}
      {showModal && (
        <div className="modal show d-block" tabIndex={-1} role="dialog">
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Confirm Deletion</h5>
                <button
                  type="button"
                  className="btn-close"
                  aria-label="Close"
                  onClick={closeModal}
                />
              </div>
              <div className="modal-body">
                <p>
                  Are you sure you want to delete{' '}
                  <strong>{deletingIds.length}</strong> users?
                </p>
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={closeModal}
                >
                  Cancel
                </button>
                <button
                  type="button"
                  className="btn btn-danger"
                  onClick={handleDelete}
                >
                  Confirm
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
