import React, { useState } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import './CreateAccount.css';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function CreateAccountPage() {
  const [username, setUsername] = useState<string>('');
  const [email, setEmail] = useState<string>('');
  const [password, setPassword] = useState<string>('');
  const navigate = useNavigate();
  function register(event: React.FormEvent) {
    event.preventDefault();
    axios
      .post('http://localhost:8081/register', { username, email, password })
      .then((res) => {
        console.log(res);
        navigate('/login', { state: { email, password } }); // Передаем email и password
      })
      .catch((err) => console.log(err));
  }

  const handleBackToLogin = () => {
    navigate('/login'); // Перенаправление на страницу логина
  };
  return (
    <div className="SignInPage">
      <motion.div
        className="PicturePart"
        initial={{ backgroundPositionX: '100%' }}
        animate={{ backgroundPositionX: '0%' }}
        exit={{ backgroundPositionX: '-100%' }}
        transition={{ duration: 1 }}
      >
        <div className="TextOverlay">
          <p>
            Welcome to HomeComfort! Join us today and start your journey to
            comfort.
          </p>
          <Button
            variant="light"
            size="lg"
            onClick={handleBackToLogin} // Добавлено событие для перехода на логин
          >
            BACK TO LOGIN
          </Button>
        </div>
      </motion.div>

      <div className="SignINBlock">
        <div className="TopPart">
          <h1>HOMECOMFORT</h1>
        </div>
        <form className="Middle" onSubmit={register}>
          <h2 className="SignUpText">Create Account</h2>
          <Form.Control
            size="lg"
            type="text"
            placeholder="Name"
            className="inputEmailPass"
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => {
              setUsername(e.target.value);
              console.log(username);
            }}
          />
          <Form.Control
            size="lg"
            type="email"
            placeholder="Email"
            className="inputEmailPass"
            onChange={(e: React.ChangeEvent<HTMLInputElement>) =>
              setEmail(e.target.value)
            }
          />
          <Form.Control
            size="lg"
            type="password"
            placeholder="Password"
            className="inputEmailPass"
            onChange={(e: React.ChangeEvent<HTMLInputElement>) =>
              setPassword(e.target.value)
            }
          />
          <Button
            variant="secondary"
            size="lg"
            className="SignInButton"
            type="submit"
          >
            CREATE ACCOUNT
          </Button>
        </form>
      </div>
    </div>
  );
}

export default CreateAccountPage;
