import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../AuthProvider/AuthProvider';
import './index.css';

interface User {
  id: number;
  name: string;
  email: string;
  lastLogin: string | null;
  status: 'active' | 'blocked';
}

const Home: React.FC = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [selectedIds, setSelectedIds] = useState<number[]>([]);
  const [showModal, setShowModal] = useState(false);
  const navigate = useNavigate();
  const { logout } = useAuth();

  const fetchUsers = useCallback(async () => {
    const token = localStorage.getItem('authToken');
    try {
      const response = await fetch('http://localhost:8081/users', {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!response.ok) {
        if (response.status === 401) navigate('/login');
        throw new Error('Failed to fetch users');
      }
      const data = await response.json();
      setUsers(data);
    } catch (error) {
      alert('Error fetching users');
    }
  }, [navigate]);

  useEffect(() => {
    fetchUsers();
  }, [fetchUsers]);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const handleDelete = async () => {
    try {
      const response = await fetch('http://localhost:8081/users/delete', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('authToken')}`,
        },
        body: JSON.stringify({ ids: selectedIds }),
      });
      if (!response.ok) throw new Error('Failed to delete users');
      const data = await response.json();
      setUsers(users.filter((user) => !selectedIds.includes(user.id)));
      setSelectedIds([]);
      alert(data.message || 'Users deleted successfully');
    } catch {
      alert('Failed to delete users');
    } finally {
      setShowModal(false);
    }
  };

  const handleBlockUnblock = async (action: 'block' | 'unblock') => {
    try {
      const response = await fetch(`http://localhost:8081/users/${action}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ids: selectedIds }),
      });
      if (!response.ok) throw new Error(`Failed to ${action} users`);
      const updatedStatus = action === 'block' ? 'blocked' : 'active';
      setUsers(
        users.map((user) =>
          selectedIds.includes(user.id)
            ? { ...user, status: updatedStatus }
            : user,
        ),
      );
      setSelectedIds([]);
      alert(`Users ${action}ed successfully`);
    } catch {
      alert(`Failed to ${action} users`);
    }
  };

  const handleSelectAll = () => {
    setSelectedIds(
      users.length === selectedIds.length ? [] : users.map((user) => user.id),
    );
  };

  const handleCheckboxChange = (id: number) => {
    setSelectedIds((prev) =>
      prev.includes(id)
        ? prev.filter((userId) => userId !== id)
        : [...prev, id],
    );
  };

  const sortedUsers = [...users].sort((a, b) =>
    (a.lastLogin ?? '').localeCompare(b.lastLogin ?? ''),
  );

  return (
    <div className="container mt-4">
      <h1>User Table</h1>
      <div>
        <button
          onClick={handleLogout}
          className="btn btn-secondary"
          type="button"
        >
          Logout
        </button>
      </div>

      <div className="toolbar mb-4">
        <button
          onClick={() => handleBlockUnblock('block')}
          className="btn btn-primary me-3"
          type="button"
        >
          Block
        </button>
        <button
          onClick={() => handleBlockUnblock('unblock')}
          className="btn btn-outline-secondary me-3"
          type="button"
        >
          Unblock
        </button>
        <button
          onClick={() => setShowModal(true)}
          className="btn btn-danger"
          type="button"
        >
          Delete
        </button>
      </div>

      <table className="table">
        <thead>
          <tr>
            <th>
              <input
                type="checkbox"
                checked={selectedIds.length === users.length} // This checks if all users are selected
                onChange={handleSelectAll}
                aria-label="Select all users"
              />
            </th>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Last Login</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {sortedUsers.map((user) => (
            <tr key={user.id}>
              <td>
                <input
                  type="checkbox"
                  checked={selectedIds.includes(user.id)}
                  onChange={() => handleCheckboxChange(user.id)}
                  aria-label={`Select user with ID ${user.id}`}
                />
              </td>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.lastLogin}</td>
              <td>{user.status}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {showModal && (
        <div className="modal show d-block" tabIndex={-1}>
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Confirm Deletion</h5>
              </div>
              <div className="modal-body">
                <p>
                  Are you sure you want to delete {selectedIds.length} users?
                </p>
              </div>
              <div className="modal-footer">
                <button
                  onClick={() => setShowModal(false)}
                  className="btn btn-secondary"
                  type="button"
                >
                  Cancel
                </button>
                <button
                  onClick={handleDelete}
                  className="btn btn-danger"
                  type="button"
                >
                  Confirm
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
