import React, { useCallback, useState } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import { motion } from 'framer-motion';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../AuthProvider/AuthProvider';

function SignInModal() {
  const location = useLocation();
  const navigate = useNavigate();
  const { login } = useAuth();

  const [values, setValues] = useState({
    email: location.state?.email || '',
    password: location.state?.password || '',
  });

  const loginUser = useCallback(
    (event: React.FormEvent) => {
      event.preventDefault();
      axios
        .post('http://localhost:8081/login', values)
        .then((res) => {
          console.log(res.data.User.status);
          if (res.data.Status === 'Success') {
            if (res.data.User.status === 'blocked') {
              // Check if the user is blocked
              alert('Your account is blocked. Please contact support.');
            } else {
              login(); // Set the auth state
              navigate('/home');
            }
          } else {
            alert('Invalid email or password');
          }
        })
        .catch((err) => {
          console.error(err);
          alert('Login failed. Please try again.');
        });
    },
    [values, navigate, login],
  );

  const handleCreateAccountClick = useCallback(() => {
    navigate('/register');
  }, [navigate]);

  return (
    <div className="SignInPage">
      <div className="SignINBlock">
        <div className="TopPart">
          <h1>HOMECOMFORT</h1>
        </div>
        <div className="Middle">
          <h2 className="SignUpText">Sign In</h2>
          <Form.Control
            size="lg"
            type="text"
            placeholder="Email"
            className="inputEmailPass"
            value={values.email}
            onChange={(e) =>
              setValues((prev) => ({ ...prev, email: e.target.value }))
            }
          />
          <Form.Control
            size="lg"
            type="password"
            placeholder="Password"
            className="inputEmailPass"
            value={values.password}
            onChange={(e) =>
              setValues((prev) => ({ ...prev, password: e.target.value }))
            }
          />
          <Button
            variant="secondary"
            size="lg"
            className="SignInButton"
            onClick={loginUser}
          >
            SIGN IN
          </Button>
        </div>
      </div>
      <motion.div
        className="PicturePart"
        initial={{ backgroundPositionX: '100%' }}
        animate={{ backgroundPositionX: '0%' }}
        exit={{ backgroundPositionX: '-100%' }}
        transition={{ duration: 1 }}
      >
        <div className="TextOverlay">
          <p>Welcome to HomeComfort! Your journey to comfort starts here.</p>
          <Button variant="light" size="lg" onClick={handleCreateAccountClick}>
            CREATE ACCOUNT
          </Button>
        </div>
      </motion.div>
    </div>
  );
}

export default SignInModal;
