import React, { useCallback, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../AuthProvider/AuthProvider';
import './index.css';

interface User {
  id: number;
  name: string;
  email: string;
  lastLogin: string | null;
  activity: number[];
  status: 'active' | 'blocked';
  token?: string;
}

const Home: React.FC = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [selectedIds, setSelectedIds] = useState<number[]>([]);
  const [selectAll, setSelectAll] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [deletingIds, setDeletingIds] = useState<number[]>([]);
  const currentUserId = 1;
  const navigate = useNavigate();
  const { logout } = useAuth();

  const fetchUsers = useCallback(async () => {
    try {
      const response = await fetch('http://localhost:8081/users', {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('authToken')}`,
        },
      });

      if (!response.ok) {
        throw new Error('Failed to fetch users');
      }

      const usersData = await response.json();
      localStorage.setItem('usersData', JSON.stringify(usersData));
      setUsers(usersData);
    } catch (error) {
      console.error(error);
      alert('Failed to fetch users. Please try again.');
    }
  }, []);

  useEffect(() => {
    fetchUsers();
  }, [fetchUsers]);

  const handleLogout = useCallback(() => {
    logout();
    navigate('/login');
  }, [logout, navigate]);

  const handleDelete = useCallback(async () => {
    const usersData = JSON.parse(localStorage.getItem('usersData') || '[]');
    try {
      const response = await fetch('http://localhost:8081/users/delete', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('authToken')}`,
        },
        body: JSON.stringify({ ids: selectedIds }),
      });

      if (!response.ok) {
        throw new Error('Failed to delete users');
      }

      const result = await response.json();
      alert(result.message);

      const updatedUsers = usersData.filter(
        (user: any) => !selectedIds.includes(user.id),
      );
      localStorage.setItem('usersData', JSON.stringify(updatedUsers));
      setUsers(updatedUsers);
      setSelectedIds([]);
    } catch (error) {
      console.error(error);
      alert('Failed to delete users. Please try again.');
    }
  }, [selectedIds]);

  const openDeleteModal = useCallback(() => {
    if (selectedIds.length === 0) {
      alert('No users selected for deletion.');
      return;
    }
    setDeletingIds(selectedIds);
    setShowModal(true);
  }, [selectedIds]);

  const closeModal = useCallback(() => {
    setShowModal(false);
    setDeletingIds([]);
  }, []);

  const handleBlockUsers = useCallback(async () => {
    const usersData = JSON.parse(localStorage.getItem('usersData') || '[]');
    const selectedUserTokens = selectedIds.map(
      (id) => usersData.find((user: any) => user.id === id)?.token,
    );

    try {
      const response = await fetch('http://localhost:8081/users/block', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('authToken')}`,
        },
        body: JSON.stringify({ ids: selectedIds, tokens: selectedUserTokens }),
      });
      console.log(response);
      if (!response.ok) {
        throw new Error('Failed to block users');
      }

      const result = await response.json();
      alert(result.message);

      const updatedUsers = usersData.map((user: any) => {
        if (selectedIds.includes(user.id)) {
          return { ...user, status: 'blocked' };
        }
        return user;
      });

      localStorage.setItem('usersData', JSON.stringify(updatedUsers));
      setUsers(updatedUsers);
      setSelectedIds([]);
    } catch (error) {
      console.error(error);
      alert('Failed to block users. Please try again.');
    }
  }, [selectedIds]);

  const handleSelectAll = useCallback(() => {
    if (selectAll) {
      setSelectedIds([]);
    } else {
      setSelectedIds(users.map((u) => u.id));
    }
    setSelectAll(!selectAll);
  }, [selectAll, users]);

  const handleCheckboxChange = useCallback(
    (id: number) => {
      const updatedSelectedIds = selectedIds.includes(id)
        ? selectedIds.filter((userId) => userId !== id)
        : [...selectedIds, id];
      setSelectedIds(updatedSelectedIds);
      setSelectAll(updatedSelectedIds.length === users.length);
    },
    [selectedIds, users.length],
  );

  return (
    <div className="container mt-4">
      <h1>User Table</h1>
      <div className="logout-container">
        <button type="button" onClick={handleLogout}>
          Logout
        </button>
      </div>
      <div className="toolbar mb-4">
        <button
          type="button"
          className="btn btn-primary me-3"
          onClick={handleBlockUsers}
        >
          Block
        </button>
        <button
          type="button"
          className="btn btn-outline-secondary me-3"
          onClick={() => console.log('Unblock logic here')}
        >
          Unblock
        </button>
        <button
          type="button"
          className="btn btn-outline-danger"
          onClick={openDeleteModal}
        >
          Delete
        </button>
      </div>
      <table className="table table-striped table-bordered">
        <thead>
          <tr>
            <th>
              <input
                type="checkbox"
                checked={selectAll}
                onChange={handleSelectAll}
                aria-label="Select user with ID"
              />
            </th>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Last Login</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr
              key={user.id}
              style={{
                backgroundColor: user.id === currentUserId ? '#f0f8ff' : '',
              }}
            >
              <td>
                <input
                  type="checkbox"
                  checked={selectedIds.includes(user.id)}
                  onChange={() => handleCheckboxChange(user.id)}
                  aria-label={`Select user with ID ${user.id}`}
                />
              </td>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.lastLogin || 'N/A'}</td>
              <td>{user.status === 'active' ? 'Active' : 'Blocked'}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {showModal && (
        <div className="modal show d-block">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Confirm Deletion</h5>
              </div>
              <div className="modal-body">
                <p>
                  Are you sure you want to delete{' '}
                  <strong>{deletingIds.length}</strong> users?
                </p>
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={closeModal}
                >
                  Cancel
                </button>
                <button
                  type="button"
                  className="btn btn-danger"
                  onClick={handleDelete}
                >
                  Confirm
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
