import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './index.css';

interface User {
  id: number;
  name: string;
  email: string;
  lastLogin: string | null;
  status: 'active' | 'blocked';
  registrationDate: string;
}

const Home: React.FC = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [selectedIds, setSelectedIds] = useState<number[]>([]);
  const [selectAll, setSelectAll] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [deletingIds, setDeletingIds] = useState<number[]>([]);
  const currentUserId = 1; // ID текущего пользователя
  const navigate = useNavigate();

  const fetchUsers = async () => {
    try {
      const response = await fetch('http://localhost:8081/users');
      const data: User[] = await response.json();

      const formattedData = data.map((user) => ({
        ...user,
        lastLogin: user.lastLogin
          ? new Date(user.lastLogin).toLocaleString()
          : 'Never',
        registrationDate: new Date(user.registrationDate).toLocaleString(),
      }));

      setUsers(formattedData);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleDelete = async () => {
    try {
      const response = await fetch('http://localhost:8081/users/delete', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ids: deletingIds }),
      });

      if (!response.ok) throw new Error('Failed to delete users.');

      if (deletingIds.includes(currentUserId)) {
        alert('Your account has been deleted. Redirecting to login...');
        localStorage.removeItem('authToken');
        navigate('/login');
        return;
      }

      setUsers(users.filter((user) => !deletingIds.includes(user.id)));
      setSelectedIds([]);
      setSelectAll(false);
      setShowModal(false);
      alert('Selected users have been deleted.');
    } catch (error) {
      console.error('Error deleting users:', error);
      alert('Failed to delete users. Please try again.');
    }
  };

  const openDeleteModal = () => {
    if (selectedIds.length === 0) {
      console.warn('No users selected for deletion.');
      return;
    }
    setDeletingIds(selectedIds);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setDeletingIds([]);
  };

  return (
    <div className="container mt-4">
      <h1>User Table</h1>
      <div className="toolbar mb-4 d-flex gap-3">
        <button
          className="btn btn-primary"
          onClick={() => console.log('Block users:', selectedIds)}
        >
          Block
        </button>
        <button
          className="btn btn-outline-secondary"
          onClick={() => console.log('Unblock users:', selectedIds)}
        >
          <i className="bi bi-unlock" /> Unblock
        </button>
        <button
          className="btn btn-outline-danger"
          onClick={openDeleteModal}
        >
          <i className="bi bi-trash" /> Delete
        </button>
      </div>
      <table className="table table-striped table-bordered">
        <thead>
          <tr>
            <th>
              <input
                type="checkbox"
                checked={selectAll}
                onChange={() => {
                  setSelectAll(!selectAll);
                  setSelectedIds(!selectAll ? users.map((u) => u.id) : []);
                }}
              />
            </th>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Last Login</th>
            <th>Status</th>
            <th>Registration Date</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>
                <input
                  type="checkbox"
                  checked={selectedIds.includes(user.id)}
                  onChange={() =>
                    setSelectedIds((prev) =>
                      prev.includes(user.id)
                        ? prev.filter((id) => id !== user.id)
                        : [...prev, user.id]
                    )
                  }
                />
              </td>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.lastLogin}</td>
              <td>{user.status === 'active' ? 'Active' : 'Blocked'}</td>
              <td>{user.registrationDate}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {showModal && (
        <div className="modal show d-block" tabIndex={-1} role="dialog">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5>Confirm Deletion</h5>
                <button
                  className="btn-close"
                  onClick={closeModal}
                  aria-label="Close"
                />
              </div>
              <div className="modal-body">
                <p>
                  Are you sure you want to delete {deletingIds.length} users?
                </p>
              </div>
              <div className="modal-footer">
                <button className="btn btn-secondary" onClick={closeModal}>
                  Cancel
                </button>
                <button className="btn btn-danger" onClick={handleDelete}>
                  Confirm
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
