import React, { useCallback, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import fetchUsersData from '../../utils/fetchUsers';
import { User } from '../../utils/interfaces';
import { useAuth } from '../AuthProvider/AuthProvider';
import './index.css';

const Home: React.FC = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [selectedIds, setSelectedIds] = useState<number[]>([]);
  const [selectAll, setSelectAll] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [deletingIds, setDeletingIds] = useState<number[]>([]);
  const [selectedUsersData, setSelectedUsersData] = useState<User[]>([]);
  const navigate = useNavigate();
  const { logout } = useAuth();
  const [userToken, setUserToken] = useState<string | null>(null);
  const [userDData, setUserDData] = useState<User | null>(null);

  // Загружаем данные пользователя из localStorage
  useEffect(() => {
    const token = localStorage.getItem('authToken');
    const userData = localStorage.getItem('userData');
    if (token) {
      setUserToken(token);
    }
    if (userData) {
      setUserDData(JSON.parse(userData)); // Загружаем данные пользователя
    }
  }, []);

  const fetchUsers = useCallback(async () => {
    if (!userToken) return;

    try {
      const data = await fetchUsersData(userToken);
      const formattedData = data.map((user: any) => {
        let lastLogin = 'Never';
        if (user.lastLogin) {
          const date = new Date(user.lastLogin);
          if (!Number.isNaN(date.getTime())) {
            lastLogin = date.toLocaleString();
          }
        }
        return { ...user, lastLogin };
      });

      setUsers(formattedData);
    } catch (error) {
      console.error(error);
      alert('Error fetching users. Please try again later.');
    }
  }, [userToken]);

  useEffect(() => {
    if (userToken) {
      fetchUsers();
    }
  }, [fetchUsers, userToken]);

  const handleLogout = useCallback(() => {
    logout();
    localStorage.removeItem('userData');
    navigate('/login');
  }, [logout, navigate]);

  const handleSelectAll = useCallback(() => {
    if (selectAll) {
      setSelectedIds([]);
    } else {
      setSelectedIds(users.map((u) => u.id));
    }
    setSelectAll(!selectAll);
  }, [selectAll, users]);

  const handleCheckboxChange = useCallback(
    (id: number) => {
      const updatedSelectedIds = selectedIds.includes(id)
        ? selectedIds.filter((userId) => userId !== id)
        : [...selectedIds, id];

      setSelectedIds(updatedSelectedIds);
      setSelectAll(updatedSelectedIds.length === users.length);

      const updatedSelectedUsersData = updatedSelectedIds
        .map((userId) => users.find((user) => user.id === userId))
        .filter((user): user is User => user !== undefined);

      setSelectedUsersData(updatedSelectedUsersData);
    },
    [selectedIds, users],
  );

  const handleUnblockUsers = async () => {
    try {
      const response = await fetch('http://localhost:8081/users/unblock', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${userToken}`,
        },
        body: JSON.stringify({ ids: selectedIds }),
      });

      if (!response.ok) {
        throw new Error('Failed to unblock users.');
      }

      alert('Selected users have been unblocked.');
      fetchUsers();
    } catch (error) {
      alert('Failed to unblock users. Please try again.');
    }
  };

  const handleDelete = useCallback(async () => {
    try {
      const response = await fetch('http://localhost:8081/users/delete', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${userToken}`,
        },
        body: JSON.stringify({ ids: selectedIds }),
      });

      if (!response.ok) {
        throw new Error('Failed to delete users');
      }

      const result = await response.json();
      alert(result.message);

      navigate('/login');
    } catch (error) {
      console.error(error);
      alert('Failed to delete users. Please try again.');
    }
  }, [selectedIds, userToken, navigate]);

  const openDeleteModal = useCallback(() => {
    if (selectedIds.length === 0) {
      alert('No users selected for deletion.');
      return;
    }
    setDeletingIds(selectedIds);
    setShowModal(true);
  }, [selectedIds]);

  const closeModal = useCallback(() => {
    setShowModal(false);
    setDeletingIds([]);
  }, []);

  const handleBlockUsers = useCallback(async () => {
    const userEmail = userDData?.email;
    if (!userEmail) {
      alert('User email is missing. Please login again.');
      return;
    }

    try {
      const emailsToBlock = selectedUsersData.map((user) => user.email);

      // Проверяем, если среди выбранных пользователей есть текущий
      if (emailsToBlock.includes(userEmail)) {
        // Если блокируем свой собственный аккаунт
        const response = await fetch('http://localhost:8081/users/block', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${userEmail}`,
          },
          body: JSON.stringify({ emails: emailsToBlock }),
        });

        if (!response.ok) {
          throw new Error('Failed to block users');
        }

        const result = await response.json();
        alert(result.message);

        logout();  // Выход из системы
        navigate('/login');  // Перенаправление на страницу логина
      } else {
        // Блокируем других пользователей
        const response = await fetch('http://localhost:8081/users/block', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${userEmail}`,
          },
          body: JSON.stringify({ emails: emailsToBlock }),
        });

        if (!response.ok) {
          throw new Error('Failed to block users');
        }

        const result = await response.json();
        alert(result.message);
        fetchUsers();
      }
    } catch (error) {
      console.error('Error while blocking users:', error);
      alert('Failed to block users. Please try again.');
    }
  }, [selectedUsersData, navigate, userDData, fetchUsers, logout]);

  const sortUsers = (usersList: User[], criteria: 'lastLogin', ascending = true): User[] => {
    const parseDate = (value: string | null | 'Never'): number => {
      if (value === 'Never' || value === null) {
        return 0;
      }
      const date = new Date(value);
      return Number.isNaN(date.getTime()) ? 0 : date.getTime();
    };

    return [...usersList].sort((a, b) => {
      const valueA = parseDate(a[criteria]);
      const valueB = parseDate(b[criteria]);

      if (valueA < valueB) return ascending ? -1 : 1;
      if (valueA > valueB) return ascending ? 1 : -1;
      return 0;
    });
  };

  const sortedUsers = sortUsers(users, 'lastLogin', false);

  return (
    <div className="container mt-4">
      <h1>User Table</h1>
      <div className="logout-container">
        <button type="button" onClick={handleLogout}>Logout</button>
      </div>
      <div className="toolbar mb-4">
        <button
          type="button"
          className="btn btn-primary me-3"
          onClick={handleBlockUsers}
        >
          Block
        </button>
        <button
          type="button"
          className="btn btn-outline-secondary me-3"
          onClick={handleUnblockUsers}
        >
          Unblock
        </button>
        <button
          type="button"
          className="btn btn-outline-danger"
          onClick={openDeleteModal}
        >
          Delete
        </button>
      </div>
      <table className="table table-striped table-bordered">
        <thead>
          <tr>
            <th>
              <input
                type="checkbox"
                checked={selectAll}
                onChange={handleSelectAll}
              />
            </th>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Status</th>
            <th>Last Login</th>
          </tr>
        </thead>
        <tbody>
          {sortedUsers.map((user) => (
            <tr key={user.id}>
              <td>
                <input
                  type="checkbox"
                  checked={selectedIds.includes(user.id)}
                  onChange={() => handleCheckboxChange(user.id)}
                />
              </td>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.status}</td>
              <td>{user.lastLogin}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {showModal && (
        <div className="modal-container">
          <div className="modal">
            <h3>Are you sure you want to delete the selected users?</h3>
            <button
              className="btn btn-danger me-2"
              onClick={handleDelete}
            >
              Yes
            </button>
            <button className="btn btn-secondary" onClick={closeModal}>
              No
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
