import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './index.css';

type User = {
  id: number;
  name: string;
  email: string;
  status: 'active' | 'blocked'; // строгая типизация статуса
  lastLogin: string | null;
  registrationDate: string;
  activity: number[];
};

const Home: React.FC = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [selectedIds, setSelectedIds] = useState<number[]>([]);
  const [selectAll, setSelectAll] = useState(false);
  const [filterDate, setFilterDate] = useState<string>('');
  const [showModal, setShowModal] = useState(false);
  const [deletingIds, setDeletingIds] = useState<number[]>([]);
  const currentUserId = 1; // ID текущего пользователя (заглушка или значение из сессии/контекста)
  const navigate = useNavigate();

  // Функция для загрузки данных пользователей
  const fetchUsers = async () => {
    try {
      const response = await fetch('http://localhost:8081/users');
      const data: User[] = await response.json();

      const formattedData = data.map((user) => {
        let lastLogin = 'Never';
        if (user.lastLogin) {
          const date = new Date(user.lastLogin);
          if (!Number.isNaN(date.getTime())) {
            lastLogin = date.toLocaleString();
          }
        }
        return {
          ...user,
          lastLogin,
          registrationDate: new Date(user.registrationDate).toLocaleString(),
        };
      });

      setUsers(formattedData);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  // Функция для блокировки пользователей
  const handleBlockUsers = async () => {
    try {
      // Выполняем запрос на блокировку пользователей
      const response = await fetch('http://localhost:8081/users/block', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ids: selectedIds }),
      });

      if (!response.ok) {
        throw new Error('Failed to block users.');
      }

      // Обновляем статус пользователей
      const updatedUsers = users.map((user): User =>
        selectedIds.includes(user.id) ? { ...user, status: 'blocked' } : user
      );
      setUsers(updatedUsers);

      // Проверяем, был ли выбран текущий пользователь
      if (selectedIds.includes(currentUserId)) {
        alert('Your account has been blocked. You will be logged out.');
        localStorage.removeItem('authToken');
        navigate('/login');
        return; // Прерывание дальнейших действий
      }

      alert('Selected users have been blocked.');
      setSelectedIds([]); // Очистка выбранных пользователей
    } catch (error) {
      console.error('Error blocking users:', error);
      alert('Failed to block users. Please try again.');
    }
  };

  // Обработчик изменения выбора всех пользователей
  const handleSelectAll = () => {
    if (selectAll) {
      setSelectedIds([]);
    } else {
      setSelectedIds(users.map((user) => user.id));
    }
    setSelectAll(!selectAll);
  };

  // Обработчик для изменения выбора отдельных пользователей
  const handleSelectUser = (userId: number) => {
    setSelectedIds((prev) =>
      prev.includes(userId)
        ? prev.filter((id) => id !== userId) // Убираем из выбранных
        : [...prev, userId] // Добавляем в выбранные
    );
  };

  // Проверка, выбраны ли все пользователи
  useEffect(() => {
    if (selectedIds.length === users.length) {
      setSelectAll(true);
    } else {
      setSelectAll(false);
    }
  }, [selectedIds, users.length]);

  return (
    <div className="container mt-4">
      <h1>User Table</h1>

      {/* Toolbar */}
      <div className="toolbar mb-4">
        <div className="d-flex justify-content-start mb-3">
          <div className="me-3">
            <input
              id="filter-date"
              type="date"
              value={filterDate}
              onChange={(e) => setFilterDate(e.target.value)}
              className="form-control"
            />
          </div>

          <button
            type="button"
            className="btn btn-primary me-2"
            onClick={handleBlockUsers}
          >
            Block
          </button>
        </div>
      </div>

      {/* Table */}
      <table className="table table-striped table-bordered">
        <thead>
          <tr>
            <th>
              <input
                type="checkbox"
                checked={selectAll}
                onChange={handleSelectAll}
                aria-label="Select all users"
              />
            </th>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Last Login</th>
            <th>Status</th>
            <th>Registration Date</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>
                <input
                  type="checkbox"
                  checked={selectedIds.includes(user.id)}
                  onChange={() => handleSelectUser(user.id)}
                  aria-label={`Select user with ID ${user.id}`}
                />
              </td>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.lastLogin}</td>
              <td>{user.status === 'active' ? 'Active' : 'Blocked'}</td>
              <td>{user.registrationDate}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
