import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './index.css';

type User = {
  id: number;
  name: string;
  email: string;
  status: 'active' | 'blocked';
  lastLogin: string | null;
  registrationDate: string;
  activity: number[];
};

const Home: React.FC = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [selectedIds, setSelectedIds] = useState<number[]>([]);
  const [selectAll, setSelectAll] = useState(false);
  const [filterDate, setFilterDate] = useState<string>('');
  const [showModal, setShowModal] = useState(false);
  const [deletingIds, setDeletingIds] = useState<number[]>([]);
  const currentUserId = Number(localStorage.getItem('currentUserId')); // ID текущего пользователя
  const navigate = useNavigate();

  // Проверка авторизации
  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (!token) {
      navigate('/login'); // Перенаправление на страницу входа, если нет токена
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('currentUserId');
    navigate('/login'); // Перенаправление на страницу входа
  };

  const fetchUsers = async () => {
    try {
      const response = await fetch('http://localhost:8081/users');
      const data: User[] = await response.json();

      const formattedData = data.map((user) => {
        let lastLogin = 'Never';
        if (user.lastLogin) {
          const date = new Date(user.lastLogin);
          if (!Number.isNaN(date.getTime())) {
            lastLogin = date.toLocaleString();
          }
        }
        return {
          ...user,
          lastLogin,
          registrationDate: new Date(user.registrationDate).toLocaleString(),
        };
      });

      setUsers(formattedData);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleDelete = async () => {
    try {
      const response = await fetch('http://localhost:8081/users/delete', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ids: deletingIds }),
      });

      if (!response.ok) {
        throw new Error('Failed to delete users.');
      }

      if (deletingIds.includes(currentUserId)) {
        alert('Your account has been deleted. Redirecting to login...');
        handleLogout(); // Автоматический выход
        return;
      }

      const updatedUsers = users.filter(
        (user) => !deletingIds.includes(user.id),
      );
      setUsers(updatedUsers);
      setSelectedIds([]);
      setSelectAll(false);
      setShowModal(false);

      alert('Selected users have been deleted.');
    } catch (error) {
      console.error('Error deleting users:', error);
      alert('Failed to delete users. Please try again.');
    }
  };

  const handleBlockUsers = async () => {
    try {
      const response = await fetch('http://localhost:8081/users/block', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ids: selectedIds }),
      });

      if (!response.ok) {
        throw new Error('Failed to block users.');
      }

      const updatedUsers = users.map(
        (user): User =>
          selectedIds.includes(user.id) ? { ...user, status: 'blocked' } : user,
      );

      setUsers(updatedUsers);
      alert('Selected users have been blocked.');
      setSelectedIds([]);
    } catch (error) {
      console.error('Error blocking users:', error);
      alert('Failed to block users. Please try again.');
    }
  };

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center">
        <h1>User Table</h1>
        <button className="btn btn-danger" onClick={handleLogout}>
          Logout
        </button>
      </div>

      {/* Toolbar */}
      <div className="toolbar mb-4">
        <div className="d-flex justify-content-start mb-3">
          <input
            id="filter-date"
            type="date"
            value={filterDate}
            onChange={(e) => setFilterDate(e.target.value)}
            className="form-control me-3"
          />
          <button
            type="button"
            className="btn btn-primary me-2"
            onClick={handleBlockUsers}
          >
            Block
          </button>
        </div>
      </div>

      {/* User Table */}
      <table className="table table-striped">
        <thead>
          <tr>
            <th>
              <input
                type="checkbox"
                checked={selectAll}
                onChange={() => {
                  setSelectAll(!selectAll);
                  setSelectedIds(!selectAll ? users.map((u) => u.id) : []);
                }}
              />
            </th>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Last Login</th>
            <th>Status</th>
            <th>Registration Date</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>
                <input
                  type="checkbox"
                  checked={selectedIds.includes(user.id)}
                  onChange={() => {
                    setSelectedIds((prev) =>
                      prev.includes(user.id)
                        ? prev.filter((id) => id !== user.id)
                        : [...prev, user.id],
                    );
                  }}
                />
              </td>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.lastLogin || 'Never'}</td>
              <td>{user.status === 'active' ? 'Active' : 'Blocked'}</td>
              <td>{user.registrationDate}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
