import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './index.css';

type User = {
  id: number;
  name: string;
  email: string;
  status: 'active' | 'blocked'; // строгая типизация статуса
  lastLogin: string | null;
  registrationDate: string;
  activity: number[];
};

const Home: React.FC = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [selectedIds, setSelectedIds] = useState<number[]>([]);
  const [selectAll, setSelectAll] = useState(false);
  const [filterDate, setFilterDate] = useState<string>('');
  const [showModal, setShowModal] = useState(false);
  const [deletingIds, setDeletingIds] = useState<number[]>([]);
  const navigate = useNavigate();

  // Получаем ID текущего пользователя из localStorage
  const currentUserId = parseInt(localStorage.getItem('currentUserId') || '0', 10);
  const [isAuthenticated, setIsAuthenticated] = useState(!!currentUserId);

  // Проверяем авторизацию
  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login'); // Если пользователь не авторизован, перенаправляем на логин
    }
  }, [isAuthenticated, navigate]);

  const fetchUsers = async () => {
    try {
      const response = await fetch('http://localhost:8081/users');
      const data: User[] = await response.json();

      const formattedData = data.map((user) => {
        let lastLogin = 'Never';
        if (user.lastLogin) {
          const date = new Date(user.lastLogin);
          if (!Number.isNaN(date.getTime())) {
            lastLogin = date.toLocaleString();
          }
        }
        return {
          ...user,
          lastLogin,
          registrationDate: new Date(user.registrationDate).toLocaleString(),
        };
      });

      setUsers(formattedData);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  useEffect(() => {
    if (isAuthenticated) {
      fetchUsers();
    }
  }, [isAuthenticated]);

  const handleLogout = () => {
    localStorage.removeItem('currentUserId'); // Очищаем данные авторизации
    setIsAuthenticated(false); // Устанавливаем состояние неавторизованного пользователя
    navigate('/login'); // Перенаправляем на страницу логина
  };

  const handleDelete = async () => {
    try {
      const response = await fetch('http://localhost:8081/users/delete', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ids: deletingIds }),
      });

      if (!response.ok) {
        throw new Error('Failed to delete users.');
      }

      // Если текущий пользователь удален, выполняем выход
      if (deletingIds.includes(currentUserId)) {
        alert('Your account has been deleted. Redirecting to login...');
        handleLogout();
        return;
      }

      // Обновляем список пользователей
      const updatedUsers = users.filter((user) => !deletingIds.includes(user.id));
      setUsers(updatedUsers);

      // Сбрасываем выбор и закрываем модальное окно
      setSelectedIds([]);
      setSelectAll(false);
      setShowModal(false);

      alert('Selected users have been deleted.');
    } catch (error) {
      console.error('Error deleting users:', error);
      alert('Failed to delete users. Please try again.');
    }
  };

  const openDeleteModal = () => {
    if (selectedIds.length === 0) {
      console.warn('No users selected for deletion.');
      return;
    }
    setDeletingIds(selectedIds);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setDeletingIds([]);
  };

  const filteredUsers = filterDate
    ? users.filter((user) =>
        new Date(user.lastLogin || '').toLocaleDateString().includes(filterDate),
      )
    : users;

  return (
    <div className="container mt-4">
      <h1>User Table</h1>
      <div className="d-flex justify-content-between mb-4">
        {/* Toolbar */}
        <div className="toolbar">
          <div className="d-flex justify-content-start">
            <div className="me-3">
              <input
                id="filter-date"
                type="date"
                value={filterDate}
                onChange={(e) => setFilterDate(e.target.value)}
                className="form-control"
              />
            </div>
            <button
              type="button"
              className="btn btn-primary me-2"
              onClick={() => {} /* Ваша логика блокировки */}
            >
              Block
            </button>
            <button
              type="button"
              className="btn btn-outline-secondary me-2"
              onClick={() => {} /* Ваша логика разблокировки */}
            >
              Unblock
            </button>
            <button
              type="button"
              className="btn btn-outline-danger"
              onClick={openDeleteModal}
            >
              Delete
            </button>
          </div>
        </div>
        {/* Logout Button */}
        <button
          className="btn btn-warning"
          onClick={handleLogout}
          aria-label="Logout button"
        >
          Logout
        </button>
      </div>

      {/* Table */}
      <table className="table table-striped table-bordered">
        <thead>
          <tr>
            <th>
              <input
                type="checkbox"
                checked={selectAll}
                onChange={() => {
                  setSelectAll(!selectAll);
                  setSelectedIds(!selectAll ? users.map((u) => u.id) : []);
                }}
              />
            </th>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Last Login</th>
            <th>Status</th>
            <th>Registration Date</th>
          </tr>
        </thead>
        <tbody>
          {filteredUsers.map((user) => (
            <tr key={user.id}>
              <td>
                <input
                  type="checkbox"
                  checked={selectedIds.includes(user.id)}
                  onChange={() => {
                    setSelectedIds((prev) =>
                      prev.includes(user.id)
                        ? prev.filter((id) => id !== user.id)
                        : [...prev, user.id],
                    );
                  }}
                />
              </td>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.lastLogin}</td>
              <td>{user.status === 'active' ? 'Active' : 'Blocked'}</td>
              <td>{user.registrationDate}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal */}
      {showModal && (
        <div className="modal show d-block" tabIndex={-1} role="dialog">
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Confirm Deletion</h5>
              </div>
              <div className="modal-body">
                <p>
                  Are you sure you want to delete{' '}
                  <strong>{deletingIds.length}</strong> users?
                </p>
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={closeModal}
                >
                  Cancel
                </button>
                <button
                  type="button"
                  className="btn btn-danger"
                  onClick={handleDelete}
                >
                  Confirm
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
